import org.junit.jupiter.api.Test;

public class AudioPostTest {


    @Test
    void createAudioShouldWork(){
        
        User Evangeline = new User("EvangelineFortuna11", "evangeline@gmail.com" , "woodvale123"); 
        String audioData = "";
        Date currentDate = new Date();
        
        AudioPost newAudioPost = new AudioPost(Evangeline.getUsername(), currentDate, audioData);

        assertNotNull(newAudioPost);
    }

    @Test
    void correctDateIsSetWhenAudioIsCreated(){
        User Evangeline = new User("EvangelineFortuna11", "evangeline@gmail.com" , "woodvale123"); 
        String audioData = "";
        Date currentDate = new Date();

        AudioPost newAudioPost = new AudioPost(Evangeline.getUsername(), currentDate, audioData);

        assertEquals(currentDate, newAudioPost.getDate());
    }

    @Test
    void correctAudioDataIsSetWhenAudioIsCreated(){
        User Evangeline = new User("EvangelineFortuna11", "evangeline@gmail.com" , "woodvale123"); 
        String audioData = "";
        Date currentDate = new Date();

        AudioPost newAudioPost = new AudioPost(Evangeline.getUsername(), currentDate, audioData);

        assertEquals(audioData, newAudioPost.getAudioData());
    }

    @Test
    void correctOwnerIsSetWhenAudioIsCreated(){
        User Evangeline = new User("EvangelineFortuna11", "evangeline@gmail.com" , "woodvale123"); 
        String audioData = "";
        Date currentDate = new Date();

        AudioPost newAudioPost = new AudioPost(Evangeline.getUsername(), currentDate, audioData);

        assertEquals(Evangeline.getUsername(), newAudioPost.getUsername());
    }

}

